// utils/getLast7Nights.js
export default function getLast7Nights(sleepSeries, currentDate) {
  const currentDateTime = new Date(currentDate + (currentDate.endsWith('Z') ? '' : 'Z'))
  const todayDateStr = currentDateTime.toISOString().split('T')[0]
  const todayMidnight = new Date(todayDateStr + 'T00:00:00Z')
  const sevenDaysAgo = new Date(todayMidnight.getTime() - (6 * 24 * 60 * 60 * 1000))

  return (sleepSeries || []).filter(day => {
    const d = new Date(day.date + 'T00:00:00Z')
    return d >= sevenDaysAgo && d <= todayMidnight && day.sleep
  })
}
