import { Themes, SolidFill, ColorRGBA } from '@lightningchart/lcjs'

// Clone CyberSpace, but make all backgrounds transparent
export const TransparentCyberSpace = {
  ...Themes.cyberSpace,

  // Main chart background
  chartBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),

  // Dashboard / panel background
  dashboardBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),
  panelBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),

  // Optional: transparent tick/grid fill if needed
  chartBoxBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),

  // Optional: transparent gauge frame backgrounds
  uiBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),
}
