// Create a new file: CustomCyberSpaceTheme.js
import { Themes, SolidFill, ColorRGBA } from '@lightningchart/lcjs';

const CustomCyberSpaceTheme = {
  ...Themes.darkGold, // Base theme without background
  
  // Copy the color schemes from CyberSpace without the background
  examples: {
    ...Themes.darkGold.examples,
    // Manually define the colors you want from CyberSpace
    seriesColorPalette: [
      '#00ff88', '#ff0080', '#0080ff', '#ff8000', 
      '#8000ff', '#80ff00', '#ff4040', '#40ff40'
    ],
  },
  
  // Ensure transparent backgrounds
  chartBackgroundFillStyle: new SolidFill({ color: ColorRGBA(0, 0, 0, 0) }),
};

export default CustomCyberSpaceTheme;
